window.$=HTMLElement.prototype.$=function(selector){
    var elems=(this==window?document:this).querySelectorAll(selector);
    return elems.length==0?null:elems.length==1?elems[0]:elems;
};
window.onload=function(){
    Auto();
};
window.onresize=function(){
    Auto();
};

//获取cookie
var nGenre=0
var nProduct=0
function fCok(){
    var reg1=/genre=/
    var reg2=/product=/
    var sCok=document.cookie;
    if(reg1.test(sCok))nGenre=sCok[reg1.exec(sCok).index+6];
    if(reg2.test(sCok))nProduct=sCok[reg2.exec(sCok).index+8];
}

//点击传入cookie
function fClick(aLis){
    for(var i=0;i<aLis.length;i++){
        aLis[i].onclick=function(){
            document.cookie='genre='+this.getAttribute('data-i');
            document.cookie='product='+0;
            window.open('productdetail.html');
        }
    }
}

//页面尺寸
function Auto(){
    var width=document.documentElement.clientWidth;
    if(width<1400){
        $('body').style.width="1400px";
        $('#banner').style.height="700px";
    }else {
        $('body').style.width=width+"px";
        $('#banner').style.height=width/2+"px";
    }
}

//轮播图信息
var aImages=[
    {"i":0,"img":"images/banner_02.png"},
    {"i":1,"img":"images/svrsbanner_02.png"},
    {"i":2,"img":"images/banner_02.png"}
];
//轮播
var oBanner={
    oImg:null,
    oAn:null,
    moved:0,
    time:null,
    canAuto:true,
    init:function(){
        this.oImg=$('#banner_img');
        this.oAn=$('#banner_an');
        this.oImg.style.width=aImages.length*100+"%";
        this.oImg.style.left=0;
        this.oAn.style.width=aImages.length*30+"px";
        this.updateView();
        $('#banner_an span')[aImages[0].i].className="hover";
        this.move();
        var me=this;
        this.oImg.onmouseover=function(){
            me.canAuto=false;
        };
        this.oImg.onmouseout=function(){
            me.canAuto=true;
        };
        this.oAn.onclick=function(e){
            var et= e.target;
            if(et.tagName=="SPAN"&&et.className==""){
                    var num=$('#banner_an .hover').getAttribute('data-i')-et.getAttribute('data-i');
                    me.time=me.tomove(num);
            }
        };
        $('.banner_left').onclick=function(){
            me.tomove(1);
        };
        $('.banner_right').onclick=function(){
            me.tomove(-1);
        }
    },
    //自动动画程序
    move:function(){
        var me=this;
        this.time=setTimeout(function(){
            if(me.canAuto){
                me.tomove(-1);
            }else{
                me.move();
            }
        },5000)
    },
    tomove:function(n){
        if(this.time!=null){
            clearTimeout(this.time);
            this.time=null;
        }
        if(n>0){
            aImages=aImages.splice(aImages.length-n,n).concat(aImages);
            this.oImg.style.left=-n*100+"%";
            this.updateView();
        }
        this.time=setTimeout(this.moveStep.bind(this,n),1);
    },
    //轮播的动画过程
    moveStep:function(n){
        var left=parseInt(this.oImg.style.left);
        left+=2*n;
        this.moved++;
        this.oImg.style.left=left+"%";
        if(this.moved<50){
            this.time=setTimeout(this.moveStep.bind(this,n),1);
        }else{
            if(n<0){
                aImages=aImages.concat(aImages.splice(0,-n));
                //console.log(aImages);
                this.updateView();
            }
            $('#banner_an span')[aImages[0].i].className="hover";
            this.oImg.style.left=0;
            this.timer = null;
            this.moved=0;
            this.move();
        }
    },
    //创建轮播图和按钮
    updateView:function(){
        var nLiWidth=100/aImages.length;
        for(var i= 0,sImgs="",sAns="";i<aImages.length;i++){
            var cas="";
            if(aImages[i].i==1){cas='class=cstservice'}
            sImgs+="<li style='width:"+nLiWidth+"%' "+cas+"><img src='"+aImages[i].img+"'><div > <span>新款</span><h1>缔造，便民服务</h1> <p>社保自助服务终端</p> <a href=''>进一步了解</a> </div> </li>";
            sAns+="<span data-i='"+i+"'></span>";
        }
        this.oImg.innerHTML=sImgs;
        this.oAn.innerHTML=sAns;
    }
};

