//点击切换
var oSecTop={
    oOl:null,
    oSpan:null,
    moved:0,
    time:null,
    canAuto:true,
    init:function(){
        this.oOl=$('#sec_top_ul ol');
        this.oSpan=$('#sec_top_span');
        this.oOl.style.width=chanpin1.length*100+"%";
        this.oOl.style.left=0;
        this.updateView();
        for(var i=0,sSpan="";i<chanpin1.length;i++){
            sSpan+="<span data-i='"+i+"'>"+chanpin1[i].genre+"</span>";
        }
        this.oSpan.innerHTML=sSpan;
        $('#sec_top_span span')[chanpin1[0].i].className="hover";
        this.move();
        var me=this;
        this.oOl.onmouseover=function(e){
            me.canAuto=false;
        };
        this.oOl.onmouseout=function(){
            me.canAuto=true;
        };
        this.oSpan.onclick=function(e){
            var et= e.target;
            if(et.tagName=="SPAN"&&et.className==""){
                var num=$('#sec_top_span .hover').getAttribute('data-i')-et.getAttribute('data-i');
                me.time=me.tomove(num);
            }
        };
        $('.sec_top_left').onclick=function(){
            me.tomove(1);
        };
        $('.sec_top_right').onclick=function(){
            me.tomove(-1);
        }
    },
    //自动动画程序
    move:function(){
        var me=this;
        this.time=setTimeout(function(){
            if(me.canAuto){
                me.tomove(-1);
            }else{
                me.move();
            }
        },5000)
    },
    tomove:function(n){
        if(this.time!=null){
            clearTimeout(this.time);
            this.time=null;
        }
        if(n>0){
            chanpin1=chanpin1.splice(chanpin1.length-n,n).concat(chanpin1);
            this.oOl.style.left=-n*100+"%";
            this.updateView();
        }
        this.time=setTimeout(this.moveStep.bind(this,n),1);
    },
    //轮播的动画过程
    moveStep:function(n){
        var left=parseInt(this.oOl.style.left);
        left+=n;
        this.moved++;
        this.oOl.style.left=left+"%";
        if(this.moved<100){
            this.time=setTimeout(this.moveStep.bind(this,n),1);
        }else{
            if(n<0){
                chanpin1=chanpin1.concat(chanpin1.splice(0,-n));
                this.updateView();
            }
            $('#sec_top_span .hover').className="";
            $('#sec_top_span span')[chanpin1[0].i].className="hover";
            this.oOl.style.left=0;
            this.timer = null;
            this.moved=0;
            this.move();
        }
    },
    //创建轮播图和按钮
    updateView:function(){
        var nUlWidth=100/chanpin1.length;
        for(var i= 0,sUl="";i<chanpin1.length;i++){
            var imgs=chanpin1[i].list;
            for(var j= 0,sLi="";j<imgs.length;j++){
                sLi+="<li><a><img src='"+chanpin1[i].list[j].Img.s+"'><div><b></b><p>"+chanpin1[i].list[j].name+"</p></div></a></li>"
            }
            sUl+="<li style='width: "+nUlWidth+"%'><ul>"+sLi+"</ul></li>";
        }
        this.oOl.innerHTML=sUl;
    }
};

