//定制服务页面中间部分图片轮播
function otherProductChange(){
    var leftBtn = $(".btn_left");
    var rightBtn = $(".btn_right");
    var othPro = $("#other_prolist");
    var ul = $("#other_prolist ul");

    ul.style.left=-1323+"px";
    var flag = false;
    var offsetW = -388-53
    function move(offsetW,n){
        flag=true;
        var newLeft = parseInt(getStyle(ul,"left"))+offsetW;
        var time = 400; //位移总时间
        var ptm = 10;  //位移间隔时间
        var speed = offsetW/(time/ptm);
        function go(){
            if(speed<0 && parseInt(getStyle(ul,"left"))>newLeft||
                speed>0&&parseInt(getStyle(ul,"left"))<newLeft)
            {
                ul.style.left=parseInt(getStyle(ul,"left"))+speed+"px";
                setTimeout(go,ptm);
            }else{
                flag=false;
                ul.style.left = newLeft+"px";
                if(n==0)
                {
                    if(newLeft<-3528)
                    {
                        ul.style.left=0+"px";
                    } 
                }
                if(n==1)
                {
                    if(newLeft>0)
                    {
                        ul.style.left=-3528+"px";
                    }
                }
            }
        }
        go();
        
    }
    rightBtn.onclick=function(){
        if(!flag)
        {
          move(-offsetW,1);  
        } 
    }
    leftBtn.onclick=function(){
        if(!flag)
        {
          move(offsetW,0);  
        } 
    }
}

function getStyle(ele, attr){
    return ele.currentStyle?ele.currentStyle[attr]:getComputedStyle(ele)[attr];
}














